export const BuySubscription = () => {
  return (
    <div className="mx-2 my-4 rounded-3xl bg-[#F4F5FA] p-20">
      <h1 className="mb-2 text-center font-merriweather text-[32px] font-black leading-9 [text-wrap:balance]">
        Get the most out of your mobile with the right subscription
      </h1>

      <p className="mb-14 text-center font-poppins text-lg font-medium [text-wrap:balance]">
        All devices come with free delivery or pickup as standard. See
        information on available shopping options for your location.
      </p>

      <div className="grid grid-cols-1 gap-10 md:grid-cols-2 lg:grid-cols-3">
        {choices.map((choice) => {
          return (
            <div
              className={`rounded-xl bg-white p-10 shadow-[-16px_-16px_0px_${choice.shadowColor}]`}
            >
              {choice.logo}

              <div className="h-[7.5rem]">
                <p className="mt-4 font-poppins text-xl font-semibold text-[#171435]">
                  {choice.title}
                </p>
                <p className="font-poppins text-sm font-medium text-[#171435]">
                  {choice.subtitle}
                </p>

                <div className="mt-4">
                  <span className="font-poppins text-2xl font-bold text-[#171435]">
                    {choice.price},-
                  </span>
                  <span className="font-poppins text-sm font-bold text-[#171435]">
                    /month
                  </span>
                </div>
              </div>

              <button className="mt-10 rounded-full border-[1px] bg-[#F4F5FA] px-6 py-4 font-poppins font-medium text-[#171435] shadow-[0px_4px_0px_0px_#E7E7FB]">
                Add subscription
              </button>
            </div>
          );
        })}
      </div>

      <div className="text-center">
        <button className="mt-14 rounded-full bg-[#171435] px-6 py-4 font-poppins font-medium text-white">
          See all subscription
        </button>
      </div>
    </div>
  );
};
